import React, { useState } from "react";
import "./UseApplication.css"; // 스타일 추가

function UseApplication() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [facility, setFacility] = useState("");
    const [date, setDate] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        // 이용신청 처리 로직
        alert("이용 신청이 완료되었습니다!");
        // 서버로 데이터 보내기 (axios 사용 가능)
    };

    return (
        <div className="use-application-container">
            <h1>이용 신청</h1>
            <form onSubmit={handleSubmit} className="application-form">
                <label>
                    이름:
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </label>
                <label>
                    이메일:
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </label>
                <label>
                    시설 선택:
                    <select
                        value={facility}
                        onChange={(e) => setFacility(e.target.value)}
                        required
                    >
                        <option value="">시설을 선택하세요</option>
                        <option value="시설 A">시설 A</option>
                        <option value="시설 B">시설 B</option>
                        <option value="시설 C">시설 C</option>
                    </select>
                </label>
                <label>
                    이용 날짜:
                    <input
                        type="date"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                        required
                    />
                </label>
                <button type="submit">신청하기</button>
            </form>
        </div>
    );
}

export default UseApplication;
