import React from "react";
import "./UseApplication.css";



export default function GoToUseApplication() {
  return (
    <div className="container">
      <div className="button-container">
        <button className="button">이용시설안내 →</button>
        <button className="button gray">층별 배치도 →</button>
        <button className="button darker-gray">좌석 배치도 →</button>
      </div>
      {facilities.map((facility) => (
        <div key={facility.floor} className="floor-section">
          <h2 className="floor-title">{facility.floor}</h2>
          <div className="card-container">
            {facility.items.map((item, index) => (
              <div key={index} className={`card ${item.color}`}>
                <div className="card-content">
                  <h3 className="card-title">{item.title}</h3>
                  <p className="card-description">{item.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
