import React, { useState } from "react";
import "./Password.css";

function Password() {
    const [input, setInput] = useState("") // 입력값 상태 관리

    // 키 입력 핸들러
    const handleKeyClick = (key) => {
        if (input.length < 5) { // 현재 입력값의 길이가 5미만인 경우
            const newInput = input + key; // 새 문자열 생성
            setInput(newInput); // 상태 업데이트

            // 조건부 이동
            if (newInput == "01234") {
                // 이동할 경로 혹은 수행할 작업
            }
        }
    };

    // 리셋 버튼 핸들러
    const handleReset = () => {
        setInput("");
    };

    // 백스페이스 버튼 핸들러
    const handleBackspace = () => {
        setInput(input.slice(0, -1));
    };

    return (
        <div className="keyboard-container">
            {/* Display */}
            <div class="display">
                <div class="black-key">0</div>
                <div class="black-key">1</div>
                <div class="black-key">2</div>
                <div class="black-key">3</div>
                <div class="black-key">4</div>
            </div>

            {/* Keypad */}
            <div class="key">1</div>
            <div class="key">2</div>
            <div class="key">3</div>
            <div class="action-key">↻</div>

            <div class="key">4</div>
            <div class="key">5</div>
            <div class="key">6</div>
            <div class="action-key">⌫</div>

            <div class="key">7</div>
            <div class="key">8</div>
            <div class="key">9</div>
            <div></div>

            <div></div>
            <div class="key">0</div>
            <div></div>
            <div></div>
        </div>
    );
}

export default Password;
