import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./Contents.css";
import Header from "../Header/Header";

function Contents() {
    const navigate = useNavigate();
    const location = useLocation();
    const [showPopup, setShowPopup] = useState(false);

    useEffect(() => {
        const params = new URLSearchParams(location.search);
        if (params.get('showPopup') === 'true') {
            setShowPopup(true);
        }
    }, [location]);

    return (
        <div className="container">
            <Header />
            <div className="bottom-section">
                <div className="highlight-section">
                    <div className="left">
                        <span className="emoji">🤩</span>
                        <div className="text">
                            <div className="highlight">잠깐!!! 입장하기 누르셨어요?</div>
                            <div>오늘 첫 "별스탬프"를 기다려요!</div>
                        </div>
                    </div>
                </div>

                {/* 팝업 영역 */}
                {showPopup && (
                    <div className="popup-overlay" onClick={() => setShowPopup(false)}>
                        <div className="popup" onClick={(e) => e.stopPropagation()}>
                            <h2>환영합니다! 🎉</h2>
                            <p>성공적으로 인증되었습니다.</p>
                            <button onClick={() => setShowPopup(false)}>닫기</button>
                        </div>
                    </div>
                )}

                <div className="top-buttons">
                    <div className="button-group">
                        <div className="group-title">시설 이용 신청</div>
                        <div className="middle-buttons">
                            <div className="middle-button"
                                style={{
                                    background: 'linear-gradient(135deg, #6a11cb, #2575fc)',
                                    color: '#fff',
                                }}>
                                <span>✨</span>
                                <div>이용신청</div>
                            </div>
                            <div className="middle-button"
                                style={{
                                    background: 'linear-gradient(135deg, #e52d27, #b31217)',
                                    color: '#fff',
                                }}>
                                <span>🏃</span>
                                <div>취소·중도퇴실</div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="footer">
                    <div className="contact-info">
                        <div>이용문의: 043-853-2021</div>
                        <div>대표문의: sumtteul@daum.net</div>
                    </div>
                    <div className="hours">
                        <div>월~금: 09:00 ~ 20:00</div>
                        <div>토요일: 10:00 ~ 18:00</div>
                        <div>일요일: 10:00 ~ 15:00</div>
                    </div>
                </div>

                <div className="playpass">청소년문화의집 숨&뜰</div>
            </div>
        </div>
    );
}

export default Contents;
