import React from "react";
import "./AttendanceTracker.css";

const AttendanceTracker = () => {
  const attendanceData = [
    { day: "일", points: 3, checked: true },
    { day: "월", points: 3, checked: true },
    { day: "화", points: 3, checked: true },
    { day: "수", points: 3, checked: true },
    { day: "목", points: 3, checked: true, bonus: 50 },
    { day: "금", points: 3, checked: true },
    { day: "토", points: 3, checked: true, bonus: 100, double: true },
  ];

  return (
    <div className="attendance-container">
      {/* 출석 현황 */}
      <div className="attendance-header">
        <div className="attendance-calendar">
          <p>이번주 출석</p>
          <h1>7일</h1>
          <span className="total-points">누적 171P</span>
        </div>
      </div>

      {/* 요일별 출석 체크 */}
      <div className="attendance-days">
        {attendanceData.map((item, index) => (
          <div key={index} className="attendance-day">
            <span className="day-label">{item.day}</span>
            {item.checked ? (
              <div className="checked-circle">
                {item.double ? <span className="double">x2</span> : "✔"}
                </div>
            ) : (
              <div className="unchecked-circle"></div>
            )}
            <span className="points">+{item.points}P</span>
            {item.bonus && <span className="bonus-points">+{item.bonus}P</span>}
          </div>
        ))}
      </div>

      {/* 출석 버튼 */}
      <button className="attendance-button" disabled>
        오늘 출석하고 3P 받기
      </button>
    </div>
  );
};

export default AttendanceTracker;
