import React, { useEffect } from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import Portal from "./Portal"; // Portal 컴포넌트가 필요함


const VISIT_COOKIE_KEY = "VisitCookie";

function Modal({ visible, onClose }) {
    useEffect(() => {
        const previousVisitDate = localStorage.getItem(VISIT_COOKIE_KEY);
        const currentVisitDate = new Date().toLocaleDateString();

        if (previousVisitDate === currentVisitDate) {
            onClose(false); // 오늘 방문 기록이 있으면 팝업 닫기
        }
    }, [onClose]);

    const handleDayClose = () => {
        const currentVisitDate = new Date().toLocaleDateString();
        localStorage.setItem(VISIT_COOKIE_KEY, currentVisitDate); // 오늘 날짜 저장
        onClose(false);
    };

    const handleClose = () => {
        onClose(false);
    };

    if (!visible) return null;

    return (
        <Portal elementId="modal-root">
            <ModalOverlay visible={visible} />
            <ModalWrapper>
                <ModalInner>
                    <ModalInner2>
                        <ImgStyle>
                            <a
                                href="https://www.pping.kr"
                                target="_blank"
                                rel="noopener noreferrer"
                            >
                            </a>
                        </ImgStyle>
                        <CloseStyle>
                            <CloseButton onClick={handleDayClose}>
                                오늘 하루 닫기
                            </CloseButton>
                            <CloseButton onClick={handleClose}>닫기</CloseButton>
                        </CloseStyle>
                    </ModalInner2>
                </ModalInner>
            </ModalWrapper>
        </Portal>
    );
}

Modal.propTypes = {
    visible: PropTypes.bool.isRequired,
    onClose: PropTypes.func.isRequired,
};

const ModalOverlay = styled.div`
    display: ${(props) => (props.visible ? "block" : "none")};
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.6);
    z-index: 999;
`;

const ModalWrapper = styled.div`
    display: ${(props) => (props.visible ? "block" : "none")};
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 1000;
    outline: 0;
`;

const ModalInner = styled.div`
    position: relative;
    top: 50%;
    transform: translateY(-50%);
    margin: 0 auto;
    width: 360px;
    background-color: #fff;
    border-radius: 10px;
    padding: 20px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
`;

const ModalInner2 = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
`;

const ImgStyle = styled.div`
    margin-bottom: 20px;
`;

const CloseStyle = styled.div`
    display: flex;
    justify-content: space-between;
    width: 100%;
`;

const CloseButton = styled.button`
    background-color: #333;
    color: #fff;
    border: none;
    border-radius: 5px;
    padding: 10px;
    cursor: pointer;
    &:hover {
        background-color: #555;
    }
`;

export default Modal;
