import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./Contents.css"; // ÏΩòÌÖêÏ∏† css
import Header from "../Header/Header"; // Ìó§Îçî ÏòÅÏó≠
import PopupPassword from "../Popup/Password/PopupPassword"; // ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†• ÌåùÏóÖ

function Contents() {
    const navigate = useNavigate();
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);

    const [showPopup, setShowPopup] = useState(queryParams.get("showPopup") === "true");
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [stampDay, setStampDay] = useState(1); // Ï≤´ Ï∂úÏÑù Ïùº Í≤ΩÏö∞ Í∏∞Î≥∏Í∞í 1

    // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú Î∞è Ï∂úÏÑù ÏùºÏ∞® ÌôïÏù∏
    useEffect(() => {
        const loggedInStatus = localStorage.getItem("isLoggedIn") === "true";
        setIsLoggedIn(loggedInStatus);

        const storedStampDay = localStorage.getItem("stampDay");
        if (storedStampDay) {
            setStampDay(parseInt(storedStampDay, 10));
        }
    }, []);

    // "ÏûÖÏû•ÌïòÍ∏∞" Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú ÎπÑÎ∞ÄÎ≤àÌò∏ ÌåùÏóÖ ÎùÑÏö∞Í∏∞
    const handleOpenPopup = () => {
        setShowPopup(true);
        navigate("?showPopup=true");
    };

    // ÌåùÏóÖ Îã´Í∏∞ (Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨)
    const handleClosePopup = () => {
        setShowPopup(false);
        navigate("");

        setIsLoggedIn(true);
        localStorage.setItem("isLoggedIn", "true");

        let nextStampDay = 1;
        if (localStorage.getItem("stampDay")) {
            nextStampDay = parseInt(localStorage.getItem("stampDay"), 10) + 1;
        }

        setStampDay(nextStampDay);
        localStorage.setItem("stampDay", nextStampDay.toString());

        window.location.reload();
    };

    // Î°úÍ∑∏ÏïÑÏõÉ Í∏∞Îä•
    const handleLogout = () => {
        setIsLoggedIn(false);
        localStorage.removeItem("isLoggedIn");
        localStorage.removeItem("stampDay");
        navigate("/contents");
    };

    return (
        <div className="container">
            <Header />
            <div className="bottom-section">
                <div className="highlight-section">
                    <div className="left">
                        <span className="emoji">ü§©</span>
                        <div className="text">
                            <div className="highlight">
                                {isLoggedIn ? "Î°úÍ∑∏ÏïÑÏõÉ ÌïòÏãúÍ≤†Ïñ¥Ïöî?" : "Ïû†Íπê!!! ÏûÖÏû•ÌïòÍ∏∞ ÎàÑÎ•¥ÏÖ®Ïñ¥Ïöî?"}
                            </div>
                            <div>
                                {isLoggedIn
                                    ? `???Îãò Ï∂úÏÑù ÏôÑÎ£å ÎêòÏóàÏäµÎãàÎã§! (${stampDay}ÏùºÏ∞®)`
                                    : "Ïò§Îäò Ï≤´ 'Î≥Ñ Ïä§ÌÉ¨ÌîÑ'Î•º Í∏∞Îã§Î†§Ïöî!"}
                            </div>
                        </div>
                    </div>
                    {isLoggedIn ? (
                        <button className="button" onClick={handleLogout}>
                            <span>üö™</span> Î°úÍ∑∏ÏïÑÏõÉ
                        </button>
                    ) : (
                        <button className="button" onClick={handleOpenPopup}>
                            <span>‚≠ê</span> ÏûÖÏû•ÌïòÍ∏∞
                        </button>
                    )}
                </div>

                <div className="top-buttons">
                    <div className="button-group">
                        <div className="group-title" style={{color: '#6a11cb'}}>ÏãúÏÑ§ Ïù¥Ïö© Ïã†Ï≤≠</div>
                        <div className="middle-buttons">
                            <div className="middle-button"
                                style={{
                                    background: 'linear-gradient(135deg, #6a11cb, #2575fc)',
                                    color: '#fff',
                                }}>
                                <span>‚ú®</span>
                                <div>Ïù¥Ïö©Ïã†Ï≤≠</div>
                            </div>
                            <div className="middle-button"
                                style={{
                                    background: 'linear-gradient(135deg, #e52d27, #b31217)',
                                    color: '#fff',
                                }}>
                                <span>üèÉ</span>
                                <div>Ï∑®ÏÜå¬∑Ï§ëÎèÑÌá¥Ïã§</div>
                            </div>
                        </div>
                    </div>
                    <div className="button-group">
                        <div className="group-title" style={{color: '#009efd'}}>ÍµêÏú° Î∞è ÌîÑÎ°úÍ∑∏Îû® Ï∂úÏÑù</div>
                        <div className="middle-buttons">
                            <div className="middle-button"
                                style={{
                                    background: 'linear-gradient(135deg, #009efd, #2af598)',
                                    color: '#fff',
                                }}>
                                <span>‚úã</span>
                                <div>Ï∂úÏÑùÏ≤¥ÌÅ¨</div>
                            </div>
                        </div>
                    </div>
                    <div className="button-group">
                        <div className="group-title" style={{ color: '#ff7f50' }}>Î¨ºÌíà ÎåÄÏó¨</div>
                        <div className="middle-buttons">
                            <div className="middle-button"
                                style={{
                                    background: 'linear-gradient(135deg, #ff7f50, #ffbc47)',
                                    color: '#fff',
                                }}>
                                <span>üé≤</span>
                                <div>ÎåÄÏó¨Ïã†Ï≤≠</div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="top-buttons">
                    <div className="button-group">
                        <div className="half-buttons-section">
                            <div className="half-button left">
                                <div className="button-content">
                                    <span>‚è≥ Ïù¥Ïö©ÌòÑÌô© Ï°∞Ìöå</span>
                                </div>
                            </div>
                            <div className="half-button right">
                                <div className="button-content">
                                    <span>üîç ÌöåÏõêÎ≤àÌò∏ Ï∞æÍ∏∞</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="footer">
                    <div className="contact-info">
                        <div>Ïù¥Ïö©Î¨∏Ïùò: 043-853-2021</div>
                        <div>ÎåÄÌëúÎ¨∏Ïùò: sumtteul@daum.net</div>
                    </div>
                    <div className="hours">
                        <div>Ïõî~Í∏à: 09:00 ~ 20:00</div>
                        <div>ÌÜ†ÏöîÏùº: 10:00 ~ 18:00</div>
                        <div>ÏùºÏöîÏùº: 10:00 ~ 15:00</div>
                    </div>
