import React, { useState, useEffect } from "react";
import CalendarPopup from "../Calendar/CalendarPopup"; // 캘린더 팝업 import 필요
import "./PopupPassword.css";

const PasswordPopup = ({ onClose }) => {
    const [input, setInput] = useState(""); // 비밀번호 입력값 상태
    const [step, setStep] = useState("password"); // 단계 상태: "password" -> "calendar"

    // 키패드 클릭 시 숫자 추가
    const handleKeyClick = (num) => {
        setInput((prev) => prev + num);
    };

    // 비밀번호 입력 변경 핸들러 (input 필드용)
    const handleChange = (e) => {
        setInput(e.target.value);
    };

    // 백스페이스 기능
    const handleBackspace = () => {
        setInput((prev) => prev.slice(0, -1));
    };

    // 입력 초기화
    const handleReset = () => {
        setInput("");
    };

    // 비밀번호가 올바른지 자동으로 확인
    useEffect(() => {
        if (input === "01234") {
            setStep("calendar"); // 비밀번호가 일치하면 자동으로 다음 단계로 이동
        }
    }, [input]); // input 값이 변경될 때마다 실행

    // 페이지 이동을 위한 함수 (필요 시 사용)
    const handleNavigate = () => {
        console.log("Navigating..."); // 실제 네비게이션 로직 추가 가능
    };

    return (
        <div className="popup-overlay">
            <div className="popup-content">
                {step === "password" && (
                    <>
                        {/* 키패드 UI */}
                        <div className="keyboard-container">
                            <div className="display">
                                {input.split("").map((char, index) => (
                                    <div key={index} className="black-key">
                                        {char}
                                    </div>
                                ))}
                            </div>

                            {/* 숫자 키패드 */}
                            <div className="key" onClick={() => handleKeyClick("1")}>1</div>
                            <div className="key" onClick={() => handleKeyClick("2")}>2</div>
                            <div className="key" onClick={() => handleKeyClick("3")}>3</div>
                            <div className="action-key" onClick={handleReset}>↻</div>

                            <div className="key" onClick={() => handleKeyClick("4")}>4</div>
                            <div className="key" onClick={() => handleKeyClick("5")}>5</div>
                            <div className="key" onClick={() => handleKeyClick("6")}>6</div>
                            <div className="action-key" onClick={handleBackspace}>⌫</div>

                            <div className="key" onClick={() => handleKeyClick("7")}>7</div>
                            <div className="key" onClick={() => handleKeyClick("8")}>8</div>
                            <div className="key" onClick={() => handleKeyClick("9")}>9</div>
                            <div></div>

                            <div></div>
                            <div className="key" onClick={() => handleKeyClick("0")}>0</div>
                            <div></div>
                            <div></div>
                        </div>

                        {/* 페이지 이동 버튼 추가 (handleNavigate 사용 예시) */}
                        <button className="navigate-btn" onClick={handleNavigate}>
                            이동하기
                        </button>
                    </>
                )}

                {/* 비밀번호 성공 시 캘린더 화면 렌더링 */}
                {step === "calendar" && <CalendarPopup onClose={onClose} />}
            </div>
        </div>
    );
};

export default PasswordPopup;
