import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

const facilities = [
  {
    floor: "4F",
    items: [
      { title: "프레임", description: "프레임(영상편집실)", color: "bg-red-500" },
      { title: "체육관", description: "A코트 B코트", color: "bg-green-500" },
    ],
  },
  {
    floor: "1F",
    items: [
      {
        title: "토이초이Room",
        description: "PC1 PC5 닌텐도2 PC2 PC6 플레이스테이션 PC3 VR스포 PC4 닌텐도1",
        color: "bg-blue-500",
      },
    ],
  },
  {
    floor: "B1F",
    items: [
      { title: "프렌즈", description: "X-BOX 포켓볼", color: "bg-teal-500" },
      { title: "사운드, 그루브", description: "그루브(댄스연습실) 사운드(음악연습실)", color: "bg-pink-500" },
      { title: "보이스", description: "노래연습실1 노래연습실2", color: "bg-yellow-500" },
    ],
  },
];

export default function GoToUseApplication() {
  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="flex flex-col space-y-4 mb-6">
        <Button className="bg-white text-black w-60">이용시설안내 →</Button>
        <Button className="bg-gray-600 w-60">층별 배치도 →</Button>
        <Button className="bg-gray-700 w-60">좌석 배치도 →</Button>
      </div>
      {facilities.map((facility) => (
        <div key={facility.floor} className="mb-6">
          <h2 className="text-2xl font-bold mb-2">{facility.floor}</h2>
          <div className="grid grid-cols-3 gap-4">
            {facility.items.map((item, index) => (
              <Card key={index} className={`p-4 ${item.color} rounded-2xl shadow-lg`}>
                <CardContent>
                  <h3 className="text-xl font-bold">{item.title}</h3>
                  <p className="text-sm mt-2">{item.description}</p>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
