import React, { useState } from "react";
import './App.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Calendar from "./Components/Main_popup/Calendar/Calendar";
import Contents from "./Components/Main/Contents/Contents";
import Password from "./Components/Password/Password";

function App() {
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  // 팝업 열기 함수
  const openPopup = () => {
    setIsPopupOpen(true);
  };

  // 팝업 닫기 함수
  const closePopup = () => {
    setIsPopupOpen(false);
  };

  return (
    <div className="App">
      <Router>
        <Routes>
          {/* 정확한 경로 설정 */}
          <Route exact path="/" element={<Contents openPopup={openPopup} />} /> {/* Calendar 팝업 제어 함수 전달 */}
          <Route exact path="/password" element={<Password />} />
        </Routes>
      </Router>

      {/* 팝업 조건부 렌더링 */}
      {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-content">
            <Calendar />
            <button onClick={closePopup} className="close-popup-btn">
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
