import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./Contents.css";
import Header from "../Header/Header";
import PopupPassword from "../Popup/Password/PopupPassword"; // ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†• ÌåùÏóÖ
import AttendanceTracker from "../Popup/Calendar/AttendanceTracker"; // Ï∂úÏÑùÏ≤¥ÌÅ¨ Ïª¥Ìè¨ÎÑåÌä∏

function Contents() {
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const [showPopup, setShowPopup] = useState(queryParams.get("showPopup") === "true");
  const [showAttendance, setShowAttendance] = useState(false); // Ï∂úÏÑùÏ≤¥ÌÅ¨ UI ÌëúÏãú Ïó¨Î∂Ä

  // "ÏûÖÏû•ÌïòÍ∏∞" Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú ÎπÑÎ∞ÄÎ≤àÌò∏ ÌåùÏóÖ ÎùÑÏö∞Í∏∞
  const handleOpenPopup = () => {
    setShowPopup(true); // showPopup ÏÉÅÌÉúÎ•º trueÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
    navigate("?showPopup=true"); // URL query parameter ÏóÖÎç∞Ïù¥Ìä∏
  };

  // ÌåùÏóÖ Îã´Í∏∞ (ÏøºÎ¶¨Ïä§Ìä∏ÎßÅ Ï†úÍ±∞)
  const handleClosePopup = () => {
    setShowPopup(false); // showPopup ÏÉÅÌÉúÎ•º falseÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
    navigate(""); // URL query parameter Ï†úÍ±∞
  };

  // Ï∂úÏÑùÏ≤¥ÌÅ¨ UI Ïó¥Í∏∞
  const handleOpenAttendance = () => {
    setShowPopup(false); // ÎπÑÎ∞ÄÎ≤àÌò∏ ÌåùÏóÖÏùÑ Îã´Ïùå
    setShowAttendance(true); // Ï∂úÏÑùÏ≤¥ÌÅ¨ UIÎ•º Ïó¥Ïùå
  };

  const handleNavigate = () => {
    navigate("/password");
  };

  return (
    <div className="container">
      <Header />
      <div className="bottom-section">
        <div className="highlight-section">
          <div className="left">
            <span className="emoji">ü§©</span>
            <div className="text">
              <div className="highlight">Ïû†Íπê!!! ÏûÖÏû•ÌïòÍ∏∞ ÎàÑÎ•¥ÏÖ®Ïñ¥Ïöî?</div>
              <div>Ïò§Îäò Ï≤´ "Î≥ÑÏä§ÌÉ¨ÌîÑ"Î•º Í∏∞Îã§Î†§Ïöî!</div>
            </div>
          </div>
          <button className="button" onClick={handleOpenPopup}>
            <span>‚≠ê</span> ÏûÖÏû•ÌïòÍ∏∞
          </button>
        </div>

        <div className="top-buttons">
          <div className="button-group">
            <div className="group-title">ÏãúÏÑ§ Ïù¥Ïö© Ïã†Ï≤≠</div>
            <div className="middle-buttons">
              <div
                className="middle-button"
                style={{
                  background: "linear-gradient(135deg, #6a11cb, #2575fc)",
                  color: "#fff",
                }}
              >
                <span>‚ú®</span>
                <div>Ïù¥Ïö©Ïã†Ï≤≠</div>
              </div>
              <div
                className="middle-button"
                style={{
                  background: "linear-gradient(135deg, #e52d27, #b31217)",
                  color: "#fff",
                }}
              >
                <span>üèÉ</span>
                <div>Ï∑®ÏÜå¬∑Ï§ëÎèÑÌá¥Ïã§</div>
              </div>
            </div>
          </div>
          <div className="button-group">
            <div className="group-title">ÍµêÏú° Ï∂úÏÑù</div>
            <div className="middle-buttons">
              <div
                className="middle-button"
                onClick={handleOpenAttendance} // Ï∂úÏÑùÏ≤¥ÌÅ¨ UI Ïó¥Í∏∞
                style={{
                  background: "linear-gradient(135deg, #009efd, #2af598)",
                  color: "#fff",
                  cursor: "pointer",
                }}
              >
                <span>‚úã</span>
                <div>Ï∂úÏÑùÏ≤¥ÌÅ¨</div>
              </div>
            </div>
          </div>
          <div className="button-group">
            <div className="group-title" style={{ color: "#ff7f50" }}>
              Î¨ºÌíà ÎåÄÏó¨
            </div>
            <div className="middle-buttons">
              <div
                className="middle-button"
                style={{
                  background: "linear-gradient(135deg, #ff7f50, #ffbc47)",
                  color: "#fff",
                }}
              >
                <span>üé≤</span>
                <div>ÎåÄÏó¨Ïã†Ï≤≠</div>
              </div>
            </div>
          </div>
        </div>

        <div className="top-buttons">
          <div className="button-group">
            <div className="half-buttons-section">
              <div className="half-button left">
                <div className="button-content">
                  <span>‚è≥ Ïù¥Ïö©ÌòÑÌô© Ï°∞Ìöå</span>
                </div>
              </div>
              <div className="half-button right">
                <div className="button-content">
                  <span>üîç ÌöåÏõêÎ≤àÌò∏ Ï∞æÍ∏∞</span>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="footer">
          <div className="contact-info">
            <div>Ïù¥Ïö©Î¨∏Ïùò: 043-853-2021</div>
            <div>ÎåÄÌëúÎ¨∏Ïùò: sumtteul@daum.net</div>
          </div>
          <div className="hours">
            <div>Ïõî~Í∏à: 09:00 ~ 20:00</div>
            <div>ÌÜ†ÏöîÏùº: 10:00 ~ 18:00</div>
            <div>ÏùºÏöîÏùº: 10:00 ~ 15:00</div>
          </div>
        </div>

        <div className="playpass">Ï≤≠ÏÜåÎÖÑÎ¨∏ÌôîÏùòÏßë Ïà®&Îú∞</div>

        {/* ÎπÑÎ∞ÄÎ≤àÌò∏ ÌåùÏóÖ ÌëúÏãú */}
        {showPopup && <PopupPassword onClose={handleClosePopup} />}

        {/* Ï∂úÏÑùÏ≤¥ÌÅ¨ UI ÌëúÏãú (Ï∂úÏÑùÏ≤¥ÌÅ¨ Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú) */}
        {showAttendance && <AttendanceTracker onClose={() => setShowAttendance(false)} />}
      </div>
    </div>
  );
}

export default Contents;
